const t=JSON.parse('{"key":"v-e15da4b2","path":"/posts/database/%E5%B9%BB%E8%AF%BB%E4%B8%8E%E4%B8%8D%E5%8F%AF%E9%87%8D%E5%A4%8D%E8%AF%BB.html","title":"幻读与不可重复读","lang":"en-US","frontmatter":{"isOriginal":true,"title":"幻读与不可重复读","tag":["mysql","幻读","不可重复读"],"category":"database","description":"关于mysql在重复读和读提交事务级别幻读和不可重复读的分析","image":"https://tech.nikolazhang.top/2020-06-04-09-49-32.png","date":"2020-06-04T00:00:00.000Z","sticky":false,"timeline":true,"article":true,"star":false,"head":[["meta",{"property":"og:url","content":"https://nikolazhang.github.io/posts/database/%E5%B9%BB%E8%AF%BB%E4%B8%8E%E4%B8%8D%E5%8F%AF%E9%87%8D%E5%A4%8D%E8%AF%BB.html"}],["meta",{"property":"og:title","content":"幻读与不可重复读"}],["meta",{"property":"og:description","content":"关于mysql在重复读和读提交事务级别幻读和不可重复读的分析"}],["meta",{"property":"og:type","content":"article"}],["meta",{"property":"og:locale","content":"en-US"}],["meta",{"property":"og:updated_time","content":"2023-06-09T00:57:17.000Z"}],["meta",{"property":"article:author","content":"我小叮当、"}],["meta",{"property":"article:tag","content":"mysql"}],["meta",{"property":"article:tag","content":"幻读"}],["meta",{"property":"article:tag","content":"不可重复读"}],["meta",{"property":"article:published_time","content":"2020-06-04T00:00:00.000Z"}],["meta",{"property":"article:modified_time","content":"2023-06-09T00:57:17.000Z"}],["script",{"type":"application/ld+json"},"{\\"@context\\":\\"https://schema.org\\",\\"@type\\":\\"Article\\",\\"headline\\":\\"幻读与不可重复读\\",\\"image\\":[\\"\\"],\\"datePublished\\":\\"2020-06-04T00:00:00.000Z\\",\\"dateModified\\":\\"2023-06-09T00:57:17.000Z\\",\\"author\\":[{\\"@type\\":\\"Person\\",\\"name\\":\\"我小叮当、\\",\\"url\\":\\"https://nikolazhang.github.io\\"}]}"]]},"headers":[{"level":2,"title":"REPEATABLE READ 与 READ COMMITTED","slug":"repeatable-read-与-read-committed","link":"#repeatable-read-与-read-committed","children":[]}],"git":{"createdTime":1686272237000,"updatedTime":1686272237000,"contributors":[{"name":"dewy yr","email":"nikolazhang@163.com","commits":1}]},"readingTime":{"minutes":1.98,"words":594},"filePathRelative":"posts/database/幻读与不可重复读.md","localizedDate":"June 4, 2020","excerpt":"<blockquote>\\n<p>关于mysql在重复读和读提交事务级别幻读和不可重复读的分析</p>\\n</blockquote>\\n<h2> REPEATABLE READ 与 READ COMMITTED</h2>\\n<p><a href=\\"https://dev.mysql.com/doc/refman/8.0/en/glossary.html\\" target=\\"_blank\\" rel=\\"noopener noreferrer\\">官方解释</a></p>\\n<blockquote>\\n<p>REPEATABLE READ\\nThe default isolation level for . It prevents any rows that are queried from being changed by other transactions, thus blocking non-repeatable reads but not phantom reads. It uses a moderately strict locking strategy so that all queries within a transaction see data from the same snapshot, that is, the data as it was at the time the transaction started. InnoDB</p>\\n<p>READ COMMITTED\\nAn isolation level that uses a locking strategy that relaxes some of the protection between transactions, in the interest of performance. Transactions cannot see uncommitted data from other transactions, but they can see data that is committed by another transaction after the current transaction started. Thus, a transaction never sees any bad data, but the data that it does see may depend to some extent on the timing of other transactions.</p>\\n</blockquote>"}');export{t as data};
